mapper.select:
select u.id as userId, concat(u.name,' ',u.phone) as userMsg, cdu.status as couponStatus, cdu.begin_time as couponUserBeginTime, cdu.end_time as couponUserEndTime, cdu.source, cdu.create_time as takeTime, cd.id as couponDetailId, cd.coupon_id as couponId, cd.name as couponName, cd.validity_type as validityType, cd.days as days, cd.use_type as useType, cd.full_money, cd.discount_money, cd.obtain from coupon_detail_user cdu left join coupon_detail cd on cdu.coupon_detail_id=cd.id left join coupon c on cd.coupon_id=c.id left join user u on cdu.user_id=u.id where 1=1 and c.shop_no=#{q.shopNo} and c.id=#{q.couponId}
and u.name like concat('%',#{q.userMsg},'%') or u.phone like concat('%',#{q.userMsg},'%')
and cdu.status=0 and cdu.begin_time <= now() and cdu.end_time >= now()
and cdu.status=#{q.couponStatus}
and cdu.status=0 and cdu.end_time <= now()
and cdu.source=0
and cdu.source=1
and cdu.source=2 and cd.obtain like concat('%','1','%')
and cdu.source=2 and cd.obtain like concat('%','2','%')
and cdu.create_time <= #{q.beginTakeTime}
and cdu.end_time >= #{q.endTakeTime}

select u.id as userId, concat(u.name,' ',u.phone) as userMsg, cdu.status as couponStatus, cdu.begin_time as couponUserBeginTime, cdu.end_time as couponUserEndTime, cdu.source, cdu.create_time as takeTime, cd.id as couponDetailId, cd.coupon_id as couponId, cd.name as couponName, cd.validity_type as validityType, cd.days as days, cd.use_type as useType, cd.full_money, cd.discount_money, cd.obtain, mc.order_sn, mc.pay_amount, mc.status as orderStatus, mc.create_time as orderAtStr from coupon_detail_user cdu left join coupon_detail cd on cdu.coupon_detail_id=cd.id left join coupon c on cd.coupon_id=c.id left join user u on cdu.user_id=u.id left join mall_cart_order mc on mc.order_sn=cdu.order_sn where 1=1 and c.shop_no=#{q.shopNo} and c.id=#{q.couponId}
and u.name like concat('%',#{q.userMsg},'%') or u.phone like concat('%',#{q.userMsg},'%')
and cdu.source=0
and cdu.source=1
and cdu.source=2 and cd.obtain like concat('%','1','%')
and cdu.source=2 and cd.obtain like concat('%','2','%')
and mc.create_time <= #{q.beginOrderTime}
and mc.create_time >= #{q.endOrderTime}
group by cdu.id
