mapper.select:
SELECT sales.id as id, sales.after_sales_no, sales.order_no, sales.order_detail_no, sales.created_at, sales.reason, sales.remark, sales.photos, sales.quantity as skuQuantity, sales.price as skuPrice, sales.money, sales.send_at, sales.receive_at, sales.status, sales.sub_status, sales.close_at, sales.apply_at, sales.audit_first_at, sales.audit_second_at, concat(u.`name`,' ',u.phone) as userName,
mo.shop_no, mo.order_no as moOrderNo, mo.status as moStatus, mo.total_money, mo.real_money, mo.deliver_method, mo.all_money-mo.real_money as shipping_money,
modetail.order_no as detailOrderNo, modetail.order_detail_no as orderDetailNo, modetail.item_no, modetail.item_name, modetail.item_cover, modetail.goods_no, modetail.sku_no, modetail.quantity as detailQuantity, modetail.price as detailPrice FROM mall_after_sales sales LEFT JOIN mall_order_detail modetail ON sales.order_detail_no=modetail.order_detail_no left join mall_order mo on modetail.order_no=mo.order_no LEFT JOIN user u ON sales.user_id=u.id WHERE sales.shop_no=#{p.shopNo}
and modetail.refund_no=#{p.refundNo}
and sales.after_sales_no = #{p.afterSalesNo}
and sales.status=#{p.status}
and sales.sub_status=1010
and sales.sub_status=1030
and (sales.sub_status=1050 or sales.sub_status=2010)
and (sales.sub_status=1080 or sales.sub_status=2020 or sales.sub_status=2030)
and (sales.sub_status=1020 or sales.sub_status=1040 or sales.sub_status=1060 or sales.sub_status=2050)
and sales.sub_status=1090
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') >= #{p.applyAtFrom}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') <= #{p.applyAtTo}
and sales.order_no=#{p.orderNo}
and (modetail.item_name LIKE concat('%',#{p.shopItem},'%') OR modetail.goods_no LIKE concat('%',#{p.shopItem},'%'))
and (u.`name` LIKE concat('%',#{p.userName},'%') OR u.phone LIKE concat('%',#{p.userName},'%'))

SELECT sales.id as id, sales.created_at, sales.after_sales_no, sales.order_no, sales.order_detail_no, sales.reason, sales.remark, sales.photos, sales.quantity as skuQuantity, sales.price as skuPrice, sales.money, sales.send_at, sales.receive_at, sales.status, sales.sub_status, sales.close_at, sales.apply_at, sales.audit_first_at, sales.audit_second_at,
mo.shop_no, mo.order_no as moOrderNo, mo.status as moStatus, mo.total_money, mo.real_money, mo.deliver_method, mo.all_money-mo.real_money as shipping_money,
modetail.order_no as detailOrderNo, modetail.order_detail_no as orderDetailNo, modetail.item_no, modetail.item_name, modetail.item_cover, modetail.goods_no, modetail.sku_no, modetail.quantity as detailQuantity, modetail.price as detailPrice FROM mall_after_sales sales LEFT JOIN mall_order_detail modetail ON sales.order_detail_no=modetail.order_detail_no LEFT JOIN user u ON sales.user_id=u.id LEFT JOIN mall_order mo ON sales.order_no=mo.order_no WHERE sales.after_sales_no=#{afterSalesNo}

SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, modetail.item_name as itemName, modetail.goods_no as goodsNo, sales.status as status, sales.sub_status as subStatus, sales.quantity as skuQuantity, sales.money as money, modetail.real_money as realMoney, mo.pay_type as payType, modetail.quantity as detailQuantity, concat(u.`name`,' ',u.phone) as userName, sales.apply_at as applyAt FROM mall_after_sales sales LEFT JOIN mall_order_detail modetail ON sales.order_detail_no=modetail.order_detail_no LEFT JOIN user u ON sales.user_id=u.id WHERE sales.shop_no=#{p.shopNo}
and modetail.refund_no=#{p.refundNo}
and sales.after_sales_no = #{p.afterSalesNo}
and sales.status=#{p.status}
and sales.sub_status=#{p.subStatus}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') >= #{p.applyAtFrom}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') <= #{p.applyAtTo}
and sales.order_no=#{p.orderNo}
and (modetail.item_name LIKE concat('%',#{p.shopItem},'%') OR modetail.goods_no LIKE concat('%',#{p.shopItem},'%'))
and (u.`name` LIKE concat('%',#{p.userName},'%') OR u.phone LIKE concat('%',#{p.userName},'%'))

SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, sales.status as status, sales.sub_status as subStatus, sales.quantity as skuQuantity, sales.money as money, sales.apply_at as applyAt, sales.audit_first_at as auditFirstAt, sales.send_at as sendAt, sales.audit_second_at as auditSecondAt, sales.photos as photos, sales.price as skuPrice, sales.remark as remark, sales.created_at as createdAt, sales.close_at as closeAt, sales.reason as reason FROM mall_after_sales sales where 1=1
and sales.order_detail_no in
#{orderDetailNo}
