mapper.select:
SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, modetail.item_name as itemName, modetail.goods_no as goodsNo, sales.status as status, sales.sub_status as subStatus, modetail.sku_no as skuNo, sales.quantity as skuQuantity, sales.price as skuPrice, sales.money as money, modetail.real_money as orderRealMoney, mo.pay_type as payType, modetail.quantity as orderQuantity, concat(u.`name`,' ',u.phone) as userName, sales.apply_at as applyAt FROM mall_after_sales sales LEFT JOIN mall_order_detail modetail ON sales.order_detail_no=modetail.order_detail_no left join mall_order mo on modetail.order_no=mo.order_no LEFT JOIN user u ON sales.user_id=u.id WHERE sales.shop_no=#{p.shopNo}
and modetail.refund_no=#{p.refundNo}
and sales.after_sales_no = #{p.afterSalesNo}
and sales.status=#{p.status}
and sales.sub_status=#{p.subStatus}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') >= #{p.applyAtFrom}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') <= #{p.applyAtTo}
and sales.order_no=#{p.orderNo}
and (modetail.item_name LIKE concat('%',#{p.shopItem},'%') OR modetail.goods_no LIKE concat('%',#{p.shopItem},'%'))
and (u.`name` LIKE concat('%',#{p.userName},'%') OR u.phone LIKE concat('%',#{p.userName},'%'))

SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, modetail.item_name as itemName, modetail.goods_no as goodsNo, sales.status as status, sales.sub_status as subStatus, sales.quantity as skuQuantity, sales.money as money, modetail.real_money as realMoney, mo.pay_type as payType, modetail.quantity as detailQuantity, concat(u.`name`,' ',u.phone) as userName, sales.apply_at as applyAt FROM mall_after_sales sales LEFT JOIN mall_order_detail modetail ON sales.order_detail_no=modetail.order_detail_no LEFT JOIN user u ON sales.user_id=u.id WHERE sales.shop_no=#{p.shopNo}
and modetail.refund_no=#{p.refundNo}
and sales.after_sales_no = #{p.afterSalesNo}
and sales.status=#{p.status}
and sales.sub_status=#{p.subStatus}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') >= #{p.applyAtFrom}
and from_unixtime(sales.apply_at, '%Y-%m-%d %H:%i:%S') <= #{p.applyAtTo}
and sales.order_no=#{p.orderNo}
and (modetail.item_name LIKE concat('%',#{p.shopItem},'%') OR modetail.goods_no LIKE concat('%',#{p.shopItem},'%'))
and (u.`name` LIKE concat('%',#{p.userName},'%') OR u.phone LIKE concat('%',#{p.userName},'%'))

SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, sales.status as status, sales.sub_status as subStatus, sales.quantity as skuQuantity, sales.money as money, sales.apply_at as applyAt, sales.audit_first_at as auditFirstAt, sales.send_at as sendAt, sales.audit_second_at as auditSecondAt, sales.photos as photos, sales.price as skuPrice, sales.remark as remark, sales.reason as reason FROM mall_after_sales sales where 1=1
and sales.order_detail_no in
#{orderDetailNo}
