mapper.select:
SELECT sales.id as id, sales.created_at, sales.after_sales_no, sales.order_no, sales.order_detail_no, sales.reason, sales.remark, sales.photos, sales.quantity as skuQuantity, sales.price as skuPrice, sales.money, sales.send_at, sales.receive_at, sales.status, sales.sub_status, sales.close_at, sales.apply_at, sales.audit_first_at, sales.audit_second_at, sales.created_time, sales.shop_no,
mo.order_no as moOrderNo, mo.status as moStatus, mo.total_money, mo.real_money, mo.deliver_method, mo.deliver_type, mo.order_type, mo.all_money, mo.all_money-mo.real_money as shipping_money,
modetail.order_no as detailOrderNo, modetail.order_detail_no as orderDetailNo, modetail.item_no, modetail.item_name, modetail.item_cover, modetail.goods_no, modetail.sku_no, modetail.quantity as detailQuantity, modetail.price as detailPrice FROM mall_after_sales sales LEFT JOIN mall_order mo ON sales.order_no=mo.order_no LEFT JOIN mall_order_detail modetail ON IF(mo.order_type=2, sales.order_detail_no=modetail.order_detail_no , modetail.order_no = mo.order_no ) LEFT JOIN user u ON sales.user_id=u.id WHERE sales.after_sales_no=#{afterSalesNo}

SELECT sales.after_sales_no as afterSalesNo, sales.order_no as orderNo, sales.order_detail_no as orderDetailNo, sales.status as status, sales.sub_status as subStatus, sales.quantity as skuQuantity, sales.money as money, sales.apply_at as applyAt, sales.audit_first_at as auditFirstAt, sales.send_at as sendAt, sales.audit_second_at as auditSecondAt, sales.photos as photos, sales.price as skuPrice, sales.remark as remark, sales.created_at as createdAt, sales.close_at as closeAt, sales.reason as reason, sales.shop_no as shopNo FROM mall_after_sales sales where 1=1
and sales.order_detail_no in
#{orderDetailNo}
and sales.order_no=#{orderNo}
