mapper.select:
select * from ( select concat_ws("︵",m.item_name,m.banners,m.share_descr,m.share_img,m.is_screen,m.item_text) as content, c.content_id,c.type ,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join mall_item m on m.item_no = c.content_id where c.type = 0 and m.status <> 50
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and m.is_screen = #{con.auditResult}
union select group_concat(f.content,fp.photo_url,f.is_screen SEPARATOR ';') as content,c.content_id,c.type,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join footprint f on f.id = c.content_id left join footprint_photo fp on fp.footprint_id = c.content_id where c.type = 1 and is_del = 0
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and f.is_screen = #{con.auditResult}
group by c.content_id union select concat_ws("︵",ifnull(o.abstract,""),ifnull(o.title,""),ifnull(o.cover,""),ifnull(o.link_url,""),ifnull(o.is_screen,""),ifnull(ond.content,"")) as content,c.content_id,c.type,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join organize_news o on o.id = c.content_id left join organize_news_detail ond on ond.organize_news_id = c.content_id where c.type = 2 and is_del = 0
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and o.is_screen = #{con.auditResult}
) as allcr LIMIT #{page},#{size}

select content_id,`type`,created_time,updated_time,auditor,audit_time,audit_result
from content_review
where content_id = #{contentId} and `type` = #{type};

select count(1) from ( select concat_ws("︵",m.item_name,m.banners,m.share_descr,m.share_img,m.is_screen,m.item_text) as content, c.content_id,c.type ,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join mall_item m on m.item_no = c.content_id where c.type = 0 and m.status <> 50
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and m.is_screen = #{con.auditResult}
union select group_concat(f.content,fp.photo_url,f.is_screen SEPARATOR ';') as content,c.content_id,c.type,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join footprint f on f.id = c.content_id left join footprint_photo fp on fp.footprint_id = c.content_id where c.type = 1 and is_del = 0
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and f.is_screen = #{con.auditResult}
group by c.content_id union select concat_ws("︵",ifnull(o.abstract,""),ifnull(o.title,""),ifnull(o.cover,""),ifnull(o.link_url,""),ifnull(o.is_screen,""),ifnull(ond.content,"")) as content,c.content_id,c.type,c.created_time, c.updated_time,c.auditor,c.audit_time from content_review c inner join organize_news o on o.id = c.content_id left join organize_news_detail ond on ond.organize_news_id = c.content_id where c.type = 2 and is_del = 0
and c.auditor = #{con.auditor}
and c.updated_time >= FROM_UNIXTIME(#{con.updateTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.updated_time <= FROM_UNIXTIME(#{con.updateTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_time >= FROM_UNIXTIME(#{con.auditTimeStart}, '%Y-%m-%d %H:%i:%S')
and c.audit_time <= FROM_UNIXTIME(#{con.auditTimeEnd}, '%Y-%m-%d %H:%i:%S')
and c.audit_result = #{con.ReviewAuditResult}
and o.is_screen = #{con.auditResult}
) as total
